import java.util.ArrayList;

class Student {
    String name; int score;
    Student(String name, int score) {
        this.name = name;
        this.score = score;
    }
}

public class Main {
    public static void main(String[] args) {
        ArrayList<Student> students = new ArrayList<>();
        students.add(new Student("Alice", 90));
        students.add(new Student("Bob", 80));
        students.add(new Student("Charlie", 100));

        double totalScore = 0;
        // Loop through the students list and sum their scores
        for (Student s : students) {
            totalScore += s.score;
        }

        // Calculate and print the average score
        double average = totalScore / students.size();
        System.out.println("Average student score: " + average);
    }
}

