public class Main {
    public static void main(String[] args) {
        String s = CompletableFuture.supplyAsync(() -> "do")
            .thenCompose(x -> CompletableFuture.supplyAsync(() -> x + "ne"))
            .thenApply(x -> x + "!")
            .join();
        System.out.println("done");
    }
}

