final class PasswordTests {
    private static final List<String> STRONG = new ArrayList<>();

    static List<String> strong() {
        return STRONG;
    }

    @BeforeEach
    void reset() {
        STRONG.clear();
    }

    @Test
    void tableDrivesExpectations() {
        Map<String, Boolean> cases = new LinkedHashMap<>();
        cases.put("Password1", Boolean.TRUE);
        cases.put("weak", Boolean.FALSE);
        cases.put("Aa123456", Boolean.TRUE);
        cases.put("NoDigits!!", Boolean.FALSE);

        for (Map.Entry<String, Boolean> entry : cases.entrySet()) {
            String password = entry.getKey();
            boolean expected = entry.getValue();
            boolean actual = PasswordStrength.isStrongPassword(password);
            if (expected && actual) {
                STRONG.add(password);
            }
            if (actual != expected) {
                throw new AssertionError("mismatch for " + password);
            }
        }

        Collections.sort(STRONG);
    }
}

