public class Main {
  public static int binarySearch(int[] nums, int target) {
    int left = 0;
    int right = nums.length - 1;
    while (left <= right) {
      int mid = left + (right - left) / 2;
      if (nums[mid] == target) {
        return mid;
      } else if (nums[mid] < target) {
        left = mid + 1;
      } else {
        right = mid - 1;
      }
    }
    return -1;
  }

  public static void main(String[] args) {
    System.out.println(binarySearch(new int[]{1, 4, 7, 9, 11, 15}, 9));
    System.out.println(binarySearch(new int[]{1, 4, 7, 9, 11, 15}, 5));
  }
}

