import java.util.*;

public class Main {
  public static int shortestPath(Map<String, List<String>> graph, String start, String goal) {
    Queue<String> queue = new ArrayDeque<>();
    Map<String, Integer> distance = new HashMap<>();
    queue.add(start);
    distance.put(start, 0);
    while (!queue.isEmpty()) {
      String node = queue.poll();
      int depth = distance.get(node);
      if (node.equals(goal)) {
        return depth;
      }
      for (String neighbor : graph.getOrDefault(node, Collections.emptyList())) {
        if (!distance.containsKey(neighbor)) {
          distance.put(neighbor, depth + 1);
          queue.add(neighbor);
        }
      }
    }
    return -1;
  }

  public static void main(String[] args) {
    Map<String, List<String>> graph = new HashMap<>();
    graph.put("A", Arrays.asList("B", "C"));
    graph.put("B", Arrays.asList("D", "E"));
    graph.put("C", Arrays.asList("F"));
    graph.put("D", Collections.emptyList());
    graph.put("E", Arrays.asList("F"));
    graph.put("F", Collections.emptyList());

    System.out.println(shortestPath(graph, "A", "F"));
    System.out.println(shortestPath(graph, "B", "C"));
