import java.util.*;

public class Main {
  static class Node {
    final String name;
    final int weight;

    Node(String name, int weight) {
      this.name = name;
      this.weight = weight;
    }
  }

  static class State {
    final String name;
    final int cost;

    State(String name, int cost) {
      this.name = name;
      this.cost = cost;
    }
  }

  public static int dijkstra(Map<String, List<Node>> graph, String start, String goal) {
    PriorityQueue<State> pq = new PriorityQueue<>(Comparator.comparingInt(s -> s.cost));
    Map<String, Integer> dist = new HashMap<>();
    pq.add(new State(start, 0));
    dist.put(start, 0);
    while (!pq.isEmpty()) {
      State current = pq.poll();
      if (current.cost > dist.getOrDefault(current.name, Integer.MAX_VALUE)) {
        continue;
      }
      if (current.name.equals(goal)) {
        return current.cost;
      }
      for (Node edge : graph.getOrDefault(current.name, Collections.emptyList())) {
        int newCost = current.cost + edge.weight;
        if (newCost < dist.getOrDefault(edge.name, Integer.MAX_VALUE)) {
          dist.put(edge.name, newCost);
          pq.add(new State(edge.name, newCost));
        }
      }
    }
    return Integer.MAX_VALUE;
  }

  public static void main(String[] args) {
    Map<String, List<Node>> graph = new HashMap<>();
    graph.put("A", Arrays.asList(new Node("B", 1), new Node("C", 4)));
    graph.put("B", Arrays.asList(new Node("C", 2), new Node("D", 5)));
    graph.put("C", Arrays.asList(new Node("D", 1)));
    graph.put("D", Arrays.asList(new Node("E", 3)));
    graph.put("E", Collections.emptyList());

    System.out.println(dijkstra(graph, "A", "E"));
  }
}

