import java.util.*;

public class Main {
  public static int lisLength(int[] nums) {
    List<Integer> tails = new ArrayList<>();
    for (int num : nums) {
      int idx = Collections.binarySearch(tails, num);
      if (idx < 0) {
        idx = -(idx + 1);
      }
      if (idx == tails.size()) {
        tails.add(num);
      } else {
        tails.set(idx, num);
      }
    }
    return tails.size();
  }

  public static void main(String[] args) {
    System.out.println(lisLength(new int[]{10, 9, 2, 5, 3, 7, 101, 18}));
    System.out.println(lisLength(new int[]{7, 7, 7, 7, 7}));
  }
}

