import java.util.*;

public class Main {
    public static void main(String[] args) {
        Deque<String> steps = new ArrayDeque<>();
        steps.addLast("init");
        steps.addLast("seed");
        steps.addLast("deploy");

        Iterator<String> descending = steps.descendingIterator();
        List<String> order = new ArrayList<>();
