import java.util.List;
import java.util.concurrent.Callable;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.Future;
import java.util.concurrent.Semaphore;
import java.util.concurrent.TimeUnit;

public class Main {
    public static void main(String[] args) throws InterruptedException, ExecutionException {
        List<String> jobs = List.of("alpha", "beta", "gamma");
        Semaphore limiter = new Semaphore(2);
        ExecutorService executor = Executors.newFixedThreadPool(jobs.size());

        try {
            List<Callable<String>> tasks = jobs.stream()
                    .<Callable<String>>map(job -> () -> {
                        limiter.acquire();
                        try {
                            long delay = switch (job) {
                                case "alpha" -> 50L;
                                case "beta" -> 80L;
                                default -> 20L;
                            };
                            TimeUnit.MILLISECONDS.sleep(delay);
                            return job + " done";
                        } finally {
                            limiter.release();
                        }
                    })
                    .toList();

            List<Future<String>> results = executor.invokeAll(tasks);
            for (Future<String> future : results) {
                System.out.println(future.get());
            }
        } finally {
            executor.shutdown();
        }
    }
}

