import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.Future;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.locks.ReentrantReadWriteLock;

public class Main {
    public static void main(String[] args) throws InterruptedException, ExecutionException {
        Map<String, String> config = new HashMap<>();
        ReentrantReadWriteLock lock = new ReentrantReadWriteLock();
        CountDownLatch writerHolding = new CountDownLatch(1);
        ExecutorService executor = Executors.newFixedThreadPool(3);

        try {
            Future<?> writer = executor.submit(() -> {
                lock.writeLock().lock();
                try {
                    writerHolding.countDown();
                    TimeUnit.MILLISECONDS.sleep(20);
                    config.put("feature", "enabled");
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                } finally {
                    lock.writeLock().unlock();
                }
            });

            writerHolding.await();

            List<Future<String>> readers = List.of(
                executor.submit(() -> {
                    TimeUnit.MILLISECONDS.sleep(5);
                    lock.readLock().lock();
                    try {
                        return "alpha reads " + config.getOrDefault("feature", "missing");
                    } finally {
                        lock.readLock().unlock();
                    }
                }),
                executor.submit(() -> {
                    TimeUnit.MILLISECONDS.sleep(10);
                    lock.readLock().lock();
                    try {
                        return "beta reads " + config.getOrDefault("feature", "missing");
                    } finally {
                        lock.readLock().unlock();
                    }
                })
            );

            writer.get();
            for (Future<String> reader : readers) {
                System.out.println(reader.get());
            }
        } finally {
            executor.shutdown();
        }
    }
}

