import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

public class Main {
    public static void main(String[] args) {
        List<Integer> latencies = new ArrayList<>(List.of(210, 190, 240, 180, 195, 205));

        Collections.sort(latencies);
        int size = latencies.size();
        double median;
        if (size % 2 == 1) {
            median = latencies.get(size / 2);
        } else {
            int right = size / 2;
            int left = right - 1;
            median = (latencies.get(left) + latencies.get(right)) / 2.0;
        }
        System.out.println("Median latency: " + median);
    }
}

