import java.util.Arrays;

public class Main {
    public static void main(String[] args) {
        double[] quarters = {1, 2, 3, 4};
        double[] signups = {120, 150, 170, 210};

        int n = quarters.length;
        double meanX = Arrays.stream(quarters).average().orElseThrow();
        double meanY = Arrays.stream(signups).average().orElseThrow();

        double numerator = 0.0;
        double denominator = 0.0;
        for (int i = 0; i < n; i++) {
            double dx = quarters[i] - meanX;
            double dy = signups[i] - meanY;
            numerator += dx * dy;
            denominator += dx * dx;
        }

        double slope = numerator / denominator;
        double intercept = meanY - slope * meanX;
        double projection = slope * 5 + intercept;

        System.out.printf("slope:%.2f%n", slope);
        System.out.printf("intercept:%.2f%n", intercept);
        System.out.printf("q5:%.2f%n", projection);
    }
}

