import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Map.Entry;
import java.util.function.Consumer;

class AuditRouter {
    private final Map<Class<?>, Consumer<Object>> handlers = new HashMap<>();
    private final List<String> emitted = new ArrayList<>();

    public AuditRouter() {
        handlers.put(Map.class, payload -> {
            @SuppressWarnings("unchecked")
            Map<String, String> record = (Map<String, String>) payload;
            String message = "map:" + record.get("status");
            emitted.add(message);
            System.out.println(message);
        });
        handlers.put(String.class, payload -> {
            String message = "str:" + ((String) payload).toUpperCase();
            emitted.add(message);
            System.out.println(message);
        });
        handlers.put(Integer.class, payload -> {
            String message = "int:" + payload;
            emitted.add(message);
            System.out.println(message);
        });
    }

    public void emit(Object payload) {
        Consumer<Object> handler = handlers.entrySet().stream()
                .filter(entry -> entry.getKey().isInstance(payload))
                .map(Entry::getValue)
                .findFirst()
                .orElseThrow(() -> new IllegalArgumentException(
                        "Unsupported type: " + payload.getClass().getSimpleName()
                ));
        handler.accept(payload);
    }

    public List<String> getEmitted() {
        return emitted;
    }
}

public class Main {
    public static void main(String[] args) {
        AuditRouter router = new AuditRouter();
        List<Object> records = List.of(
                Map.of("status", "ok"),
                "retry later",
                Map.of("status", "error"),
                2
        );

        records.forEach(router::emit);
    }
}

