import java.util.concurrent.SubmissionPublisher;

public class Main {
    public static void main(String[] args) throws Exception {
        SubmissionPublisher<String> publisher = new SubmissionPublisher<>();
        StringBuilder log = new StringBuilder();
        CountDownLatch done = new CountDownLatch(1);

        Flow.Subscriber<String> subscriber = new Flow.Subscriber<>() {
            private Flow.Subscription subscription;

            @Override
            public void onSubscribe(Flow.Subscription subscription) {
                this.subscription = subscription;
                subscription.request(1);
            }

            @Override
            public void onNext(String item) {
                log.append("onNext: ").append(item).append(System.lineSeparator());
                subscription.request(1);
            }

            @Override
            public void onError(Throwable throwable) {
                log.append("error: ").append(throwable.getMessage()).append(System.lineSeparator());
                done.countDown();
            }

            @Override
            public void onComplete() {
                log.append("complete").append(System.lineSeparator());
                done.countDown();
            }
        };

        publisher.subscribe(subscriber);
        publisher.submit("deploy");
        publisher.submit("done");
        publisher.close();

        done.await();
        System.out.print(log.toString());
    }
}

