import java.text.Normalizer;
import java.util.Locale;

public class Main {
    public static void main(String[] args) {
        String raw = "Syst\u00E9m Caf\u00E9";
        String decomposed = Normalizer.normalize(raw, Normalizer.Form.NFD);
        StringBuilder ascii = new StringBuilder();
        decomposed.codePoints()
                .filter(cp -> Character.getType(cp) != Character.NON_SPACING_MARK)
                .forEach(ascii::appendCodePoint);
        String slug = ascii.toString()
                .toLowerCase(Locale.ROOT)
                .replace(" ", "-");
        System.out.println(slug);
    }
}

