import java.util.function.Supplier;

public class Main {
    public static void main(String[] args) {
        attempt = 0;

        // Use retry method
        try {
            String result = retry(() -> unreliableOperation(), 3);
            System.out.println("Result: " + result);
        } catch (Exception e) {
            System.out.println("Failed after retries: " + e.getMessage());
        }
    }

    private static int attempt = 0;

    private static String unreliableOperation() {
        attempt++;
        System.out.println("Attempt " + attempt);
        if (attempt < 2) {
            throw new RuntimeException("Failure");
        }
        return "Success";
    }

    private static <T> T retry(Supplier<T> operation, int maxAttempts) {
        RuntimeException lastException = null;

        for (int i = 0; i < maxAttempts; i++) {
            try {
                return operation.get();
            } catch (RuntimeException e) {
                lastException = e;
                if (i < maxAttempts - 1) {
                    System.out.println("Retry " + (i + 1) + " after failure");
                }
            }
        }

        throw new RuntimeException("Max attempts reached", lastException);
    }
}

