public class Main {
    static class TokenBucket {
        private int tokens;
        private final int capacity;
        private final int refillRate;
        private long lastRefill;

        public TokenBucket(int capacity, int refillRate) {
            this.capacity = capacity;
            this.refillRate = refillRate;
            this.tokens = capacity;
            this.lastRefill = System.currentTimeMillis();
        }

        private void refill() {
            long now = System.currentTimeMillis();
            long timePassed = now - lastRefill;
            int tokensToAdd = (int) (timePassed * refillRate / 1000);

            if (tokensToAdd > 0) {
                tokens = Math.min(capacity, tokens + tokensToAdd);
                lastRefill = now;
            }
        }

        public boolean tryConsume(int tokensNeeded) {
            refill();
            if (tokens >= tokensNeeded) {
                tokens -= tokensNeeded;
                return true;
            }
            return false;
        }
    }

    public static void main(String[] args) throws InterruptedException {
        TokenBucket bucket = new TokenBucket(3, 1);  // 3 capacity, 1 token/sec

        // Consume all tokens
        for (int i = 0; i < 4; i++) {
            System.out.println("Request " + (i + 1) + ": " + bucket.tryConsume(1));
        }

        // Wait for refill
        Thread.sleep(1100);
        System.out.println("After 1s: " + bucket.tryConsume(1));
    }
}

