import java.util.*;

public class Main {
    static class SlidingWindowRateLimiter {
        private final int maxRequests;
        private final long windowMillis;
        private final Deque<Long> requestTimes = new LinkedList<>();

        public SlidingWindowRateLimiter(int maxRequests, long windowMillis) {
            this.maxRequests = maxRequests;
            this.windowMillis = windowMillis;
        }

        public boolean allowRequest() {
            long now = System.currentTimeMillis();

            // Remove requests outside window
            while (!requestTimes.isEmpty() && now - requestTimes.peekFirst() > windowMillis) {
                requestTimes.pollFirst();
            }

            // Check if under limit
            if (requestTimes.size() < maxRequests) {
                requestTimes.addLast(now);
                return true;
            }

            return false;
        }
    }

    public static void main(String[] args) throws InterruptedException {
        SlidingWindowRateLimiter limiter = new SlidingWindowRateLimiter(3, 1000);

        // Make requests
        for (int i = 0; i < 4; i++) {
            System.out.println("Request " + (i + 1) + ": " + limiter.allowRequest());
        }

        Thread.sleep(1100);
        System.out.println("After 1s: " + limiter.allowRequest());
    }
}

