public class Main {
    interface Command {
        void execute();
        void undo();
    }

    static class Calculator {
        private int value = 0;

        public void add(int amount) {
            value += amount;
        }

        public void subtract(int amount) {
            value -= amount;
        }

        public int getValue() {
            return value;
        }
    }

    static class AddCommand implements Command {
        private Calculator calculator;
        private int amount;

        public AddCommand(Calculator calculator, int amount) {
            this.calculator = calculator;
            this.amount = amount;
        }

        public void execute() {
            calculator.add(amount);
        }

        public void undo() {
            calculator.subtract(amount);
        }
    }

    static class SubtractCommand implements Command {
        private Calculator calculator;
        private int amount;

        public SubtractCommand(Calculator calculator, int amount) {
            this.calculator = calculator;
            this.amount = amount;
        }

        public void execute() {
            calculator.subtract(amount);
        }

        public void undo() {
            calculator.add(amount);
        }
    }

    public static void main(String[] args) {
        Calculator calc = new Calculator();
        Stack<Command> history = new Stack<>();

        // Execute commands
        Command add5 = new AddCommand(calc, 5);
        add5.execute();
        history.push(add5);
        System.out.println("After +5: " + calc.getValue());

        Command sub3 = new SubtractCommand(calc, 3);
        sub3.execute();
        history.push(sub3);
        System.out.println("After -3: " + calc.getValue());

        // Undo
        history.pop().undo();
        System.out.println("After undo: " + calc.getValue());

        history.pop().undo();
        System.out.println("After undo: " + calc.getValue());
    }
}

