import java.util.*;

public class Main {
    interface Command {
        void execute();
    }

    static class Task {
        public void print(String message) {
            System.out.println("Task: " + message);
        }

        public void save(String data) {
            System.out.println("Saved: " + data);
        }
    }

    static class PrintCommand implements Command {
        private Task task;
        private String message;

        public PrintCommand(Task task, String message) {
            this.task = task;
            this.message = message;
        }

        public void execute() {
            task.print(message);
        }
    }

    static class SaveCommand implements Command {
        private Task task;
        private String data;

        public SaveCommand(Task task, String data) {
            this.task = task;
            this.data = data;
        }

        public void execute() {
            task.save(data);
        }
    }

    static class CommandQueue {
        private Queue<Command> queue = new LinkedList<>();

        public void addCommand(Command command) {
            queue.offer(command);
        }

        public void executeAll() {
            while (!queue.isEmpty()) {
                Command command = queue.poll();
                command.execute();
            }
        }
    }

    public static void main(String[] args) {
        Task task = new Task();
        CommandQueue commandQueue = new CommandQueue();

        // Queue commands
        commandQueue.addCommand(new PrintCommand(task, "Hello"));
        commandQueue.addCommand(new SaveCommand(task, "data.txt"));
        commandQueue.addCommand(new PrintCommand(task, "Done"));

        // Execute all
        System.out.println("Executing queued commands:");
        commandQueue.executeAll();
    }
}

