import java.util.*;
import java.util.function.Consumer;

public class Main {
    static class Event {
        private final String type;
        private final Object data;

        public Event(String type, Object data) {
            this.type = type;
            this.data = data;
        }

        public String getType() { return type; }
        public Object getData() { return data; }
    }

    static class EventBus {
        private Map<String, List<Consumer<Event>>> listeners = new HashMap<>();

        public void on(String eventType, Consumer<Event> listener) {
            listeners.computeIfAbsent(eventType, k -> new ArrayList<>()).add(listener);
        }

        public void emit(Event event) {
            List<Consumer<Event>> eventListeners = listeners.get(event.getType());
            if (eventListeners != null) {
                for (Consumer<Event> listener : eventListeners) {
                    listener.accept(event);
                }
            }
        }

        public void off(String eventType, Consumer<Event> listener) {
            List<Consumer<Event>> eventListeners = listeners.get(eventType);
            if (eventListeners != null) {
                eventListeners.remove(listener);
            }
        }
    }

    public static void main(String[] args) {
        EventBus bus = new EventBus();

        // Register listeners
        bus.on("user.login", event -> {
            System.out.println("User logged in: " + event.getData());
        });

        bus.on("user.logout", event -> {
            System.out.println("User logged out: " + event.getData());
        });

        // Emit events
        bus.emit(new Event("user.login", "alice"));
        bus.emit(new Event("user.logout", "alice"));
        bus.emit(new Event("unknown", "ignored"));
    }
}

