import java.util.ArrayList;
import java.util.List;

public class Main {
    static class Team {
        private List<String> members;

        public Team() {
            this.members = new ArrayList<>();
        }

        public Team shallowCopy() {
            Team copy = new Team();
            copy.members = this.members;
            return copy;
        }

        public Team deepCopy() {
            Team copy = new Team();
            copy.members = new ArrayList<>(this.members);
            return copy;
        }

        public void addMember(String name) {
            members.add(name);
        }

        public int getSize() {
            return members.size();
        }
    }

    public static void main(String[] args) {
        Team original = new Team();
        original.addMember("Alice");

        Team copy = original.deepCopy();
        copy.addMember("Bob");

        System.out.println(original.getSize());
        System.out.println(copy.getSize());
    }
}

