            System.out.println("Authenticating " + user);
            return true;
        }
    }

    static class Authorization {
        public void grantAccess(String user) {
            System.out.println("Granting access to " + user);
        }
    }

    static class Logger {
        public void log(String msg) {
            System.out.println("Log: " + msg);
        }
    }

    static class SecurityFacade {
        private Authentication auth;
        private Authorization authz;
        private Logger logger;

        public SecurityFacade() {
            this.auth = new Authentication();
            this.authz = new Authorization();
            this.logger = new Logger();
        }

        public void login(String user, String pass) {
            if (auth.verify(user, pass)) {
                authz.grantAccess(user);
                logger.log(user + " logged in");
            }
        }
    }

    public static void main(String[] args) {
        SecurityFacade security = new SecurityFacade();
        security.login("admin", "pass");
    }
}

