public class Main {
    interface CompressionStrategy {
        void compress(String file);
    }

    static class ZipCompression implements CompressionStrategy {
        public void compress(String file) {
            System.out.println("ZIP: " + file);
        }
    }

    static class RarCompression implements CompressionStrategy {
        public void compress(String file) {
            System.out.println("RAR: " + file);
        }
    }

    static class FileCompressor {
        public void compress(String file, int size) {
            CompressionStrategy strategy;
            if (size < 100) {
                strategy = new ZipCompression();
            } else {
                strategy = new RarCompression();
            }
            strategy.compress(file);
        }
    }

    public static void main(String[] args) {
        FileCompressor compressor = new FileCompressor();
        compressor.compress("small.txt", 50);
        compressor.compress("large.txt", 500);
    }
}

