interface FileSystemComponent {
    void display(int indent);
    int getSize();
}

class File implements FileSystemComponent {
    private String name;
    private int size;

    public File(String name, int size) {
        this.name = name;
        this.size = size;
    }

    public void display(int indent) {
        System.out.println("  ".repeat(indent) + "File: " + name + " (" + size + " KB)");
    }

    public int getSize() {
        return size;
    }
}

class Folder implements FileSystemComponent {
    private String name;
    private java.util.List<FileSystemComponent> components = new java.util.ArrayList<>();

    public Folder(String name) {
        this.name = name;
    }

    public void add(FileSystemComponent component) {
        components.add(component);
    }

    public void display(int indent) {
        System.out.println("  ".repeat(indent) + "Folder: " + name);
        for (FileSystemComponent component : components) {
            component.display(indent + 1);
        }
    }

    public int getSize() {
        int total = 0;
        for (FileSystemComponent component : components) {
            total += component.getSize();
        }
        return total;
    }
}

