public class Main {
    public static void main(String[] args) {
        Shape circle = new Circle(5);
        Shape rectangle = new Rectangle(4, 6);

        ShapeVisitor areaCalculator = new AreaCalculator();
        ShapeVisitor perimeterCalculator = new PerimeterCalculator();

        System.out.println("Circle area: " + circle.accept(areaCalculator));
        System.out.println("Circle perimeter: " + circle.accept(perimeterCalculator));

        System.out.println("Rectangle area: " + rectangle.accept(areaCalculator));
        System.out.println("Rectangle perimeter: " + rectangle.accept(perimeterCalculator));
    }
}

interface ShapeVisitor {
    double visit(Circle circle);
    double visit(Rectangle rectangle);
}

interface Shape {
    double accept(ShapeVisitor visitor);
}

class Circle implements Shape {
    private double radius;

    public Circle(double radius) {
        this.radius = radius;
    }

    public double getRadius() {
        return radius;
    }

    public double accept(ShapeVisitor visitor) {
        return visitor.visit(this);
    }
}

class Rectangle implements Shape {
    private double width;
    private double height;

    public Rectangle(double width, double height) {
        this.width = width;
        this.height = height;
    }

    public double getWidth() {
        return width;
    }

    public double getHeight() {
        return height;
    }

    public double accept(ShapeVisitor visitor) {
        return visitor.visit(this);
    }
}

class AreaCalculator implements ShapeVisitor {
    public double visit(Circle circle) {
        return Math.PI * circle.getRadius() * circle.getRadius();
    }

    public double visit(Rectangle rectangle) {
        return rectangle.getWidth() * rectangle.getHeight();
    }
}

class PerimeterCalculator implements ShapeVisitor {
    public double visit(Circle circle) {
        return 2 * Math.PI * circle.getRadius();
    }

    public double visit(Rectangle rectangle) {
        return 2 * (rectangle.getWidth() + rectangle.getHeight());
    }
}

