public class Main {
    public static void main(String[] args) {
        String html = "<html><title>Page</title><a href='http://example.com'>Link</a></html>";
        HTMLParser parser = new HTMLParser(html);
        System.out.println("Title: " + parser.getTitle());
        System.out.println("Links: " + parser.getLinks());
    }
}

class HTMLParser {
    private String html;

    public HTMLParser(String html) { this.html = html; }

    public String getTitle() {
        int start = html.indexOf("<title>") + 7;
        int end = html.indexOf("</title>");
        return start > 6 && end > start ? html.substring(start, end) : "";
    }

    public java.util.List<String> getLinks() {
        java.util.List<String> links = new java.util.ArrayList<>();
        int pos = 0;
        while ((pos = html.indexOf("href='", pos)) != -1) {
            int start = pos + 6;
            int end = html.indexOf("'", start);
            links.add(html.substring(start, end));
            pos = end;
        }
        return links;
    }
}

