        EventStore store = new EventStore();

        store.append(new AccountCreated("ACC1", 0));
        store.append(new MoneyDeposited("ACC1", 100));
        store.append(new MoneyWithdrawn("ACC1", 30));

        Account account = store.rebuild("ACC1");
        System.out.println("Balance: " + account.balance);
    }
}

interface Event { String getAccountId(); }

class AccountCreated implements Event {
    String id;
    int initial;
    AccountCreated(String id, int initial) { this.id = id; this.initial = initial; }
    public String getAccountId() { return id; }
}

class MoneyDeposited implements Event {
    String id;
    int amount;
    MoneyDeposited(String id, int amount) { this.id = id; this.amount = amount; }
    public String getAccountId() { return id; }
}

class MoneyWithdrawn implements Event {
    String id;
    int amount;
    MoneyWithdrawn(String id, int amount) { this.id = id; this.amount = amount; }
    public String getAccountId() { return id; }
}

class Account {
    int balance = 0;
    void apply(Event e) {
        if (e instanceof MoneyDeposited) balance += ((MoneyDeposited)e).amount;
        if (e instanceof MoneyWithdrawn) balance -= ((MoneyWithdrawn)e).amount;
    }
}

class EventStore {
    java.util.List<Event> events = new java.util.ArrayList<>();

    void append(Event e) {
        events.add(e);
        System.out.println("Event stored: " + e.getClass().getSimpleName());
    }

    Account rebuild(String id) {
        Account acc = new Account();
        for (Event e : events) {
            if (e.getAccountId().equals(id)) acc.apply(e);
        }
        return acc;
    }
}

