class CQRS {
    // Write model
    java.util.List<String> writeModel = new java.util.ArrayList<>();
    // Read model (optimized for queries)
    int userCount = 0;

    void executeCommand(Command cmd) {
        if (cmd instanceof CreateUser) {
            writeModel.add(((CreateUser)cmd).name);
            userCount++;
            System.out.println("User created: " + ((CreateUser)cmd).name);
        }
    }

    <T> T executeQuery(Query<T> query) {
        if (query instanceof GetUserCount) {
            return (T) Integer.valueOf(userCount);
        }
        return null;
    }
}

