public class Main {
    public static void main(String[] args) {
        UnitOfWork uow = new UnitOfWork();

        uow.registerNew(new User(1, "Alice"));
        uow.registerNew(new User(2, "Bob"));
        uow.registerDirty(new User(1, "Alice Updated"));

        uow.commit();
    }
}

class User {
    int id;
    String name;
    User(int id, String name) { this.id = id; this.name = name; }
}

class UnitOfWork {
    java.util.List<User> newObjects = new java.util.ArrayList<>();
    java.util.List<User> dirtyObjects = new java.util.ArrayList<>();
    java.util.List<User> deletedObjects = new java.util.ArrayList<>();

    void registerNew(User obj) {
        newObjects.add(obj);
        System.out.println("Registered new: " + obj.name);
    }

    void registerDirty(User obj) {
        dirtyObjects.add(obj);
        System.out.println("Registered dirty: " + obj.name);
    }

    void registerDeleted(User obj) {
        deletedObjects.add(obj);
    }

    void commit() {
        System.out.println("\nCommitting transaction:");
        for (User u : newObjects) System.out.println("  INSERT " + u.name);
        for (User u : dirtyObjects) System.out.println("  UPDATE " + u.name);
        for (User u : deletedObjects) System.out.println("  DELETE " + u.name);
        System.out.println("Transaction committed!");
        clear();
    }

    void clear() {
