    }
}

interface Specification<T> {
    boolean isSatisfiedBy(T item);
    default Specification<T> and(Specification<T> other) {
        return item -> this.isSatisfiedBy(item) && other.isSatisfiedBy(item);
    }
}

class User {
    int age;
    boolean premium;
    User(int age, boolean premium) { this.age = age; this.premium = premium; }
}

class AgeSpec implements Specification<User> {
    int minAge;
    AgeSpec(int minAge) { this.minAge = minAge; }
    public boolean isSatisfiedBy(User user) { return user.age >= minAge; }
}

class PremiumSpec implements Specification<User> {
    public boolean isSatisfiedBy(User user) { return user.premium; }
}

