    public static void main(String[] args) {
        EventBus bus = new EventBus();
        bus.subscribe(OrderCreated.class, e -> 
            System.out.println("Handler: Order " + e.orderId + " created"));

        bus.publish(new OrderCreated("ORD-1"));
        bus.publish(new OrderShipped("ORD-1"));
    }
}

interface DomainEvent {}

class OrderCreated implements DomainEvent {
    String orderId;
    OrderCreated(String id) { this.orderId = id; }
}

class OrderShipped implements DomainEvent {
    String orderId;
    OrderShipped(String id) { this.orderId = id; }
}

class EventBus {
    java.util.Map<Class, java.util.function.Consumer> handlers = new java.util.HashMap<>();

    <T extends DomainEvent> void subscribe(Class<T> eventType, java.util.function.Consumer<T> handler) {
        handlers.put(eventType, handler);
    }

    void publish(DomainEvent event) {
        System.out.println("Event: " + event.getClass().getSimpleName());
        java.util.function.Consumer handler = handlers.get(event.getClass());
        if (handler != null) handler.accept(event);
    }
}

