public class Main {
    public static void main(String[] args) {
        DataService service = new DataService();
        Page page1 = service.getPage(1, 2);
        Page page2 = service.getPage(2, 2);

        System.out.println("Page 1: " + page1.items);
        System.out.println("Page 2: " + page2.items);
        System.out.println("Total: " + page2.total);
    }
}

class Page {
    java.util.List<String> items;
    int pageNumber;
    int pageSize;
    int total;

    Page(java.util.List<String> items, int pageNumber, int pageSize, int total) {
        this.items = items;
        this.pageNumber = pageNumber;
        this.pageSize = pageSize;
        this.total = total;
    }
}

class DataService {
    java.util.List<String> allData = java.util.Arrays.asList("A", "B", "C", "D", "E");

    Page getPage(int pageNumber, int pageSize) {
        int start = (pageNumber - 1) * pageSize;
        int end = Math.min(start + pageSize, allData.size());
        java.util.List<String> pageData = allData.subList(start, end);
        return new Page(pageData, pageNumber, pageSize, allData.size());
    }
}

