public class Main {
    public static void main(String[] args) {
        S3Client s3 = new S3Client("my-bucket");

        // Upload file
        s3.putObject("documents/report.pdf", "File content here");
        System.out.println("File uploaded successfully");

        // List objects
        System.out.println("Bucket contents:");
        for (String key : s3.listObjects()) {
            System.out.println("  - " + key);
        }

        // Get file
        String content = s3.getObject("documents/report.pdf");
        System.out.println("Retrieved: " + content);
    }
}

class S3Client {
    private String bucketName;
    private java.util.Map<String, String> storage = new java.util.HashMap<>();

    public S3Client(String bucketName) {
        this.bucketName = bucketName;
    }

    public void putObject(String key, String content) {
        storage.put(key, content);
        System.out.println("PUT s3://" + bucketName + "/" + key);
    }

    public String getObject(String key) {
        return storage.getOrDefault(key, "File not found");
    }

    public java.util.List<String> listObjects() {
        return new java.util.ArrayList<>(storage.keySet());
    }

    public void deleteObject(String key) {
        storage.remove(key);
        System.out.println("DELETE s3://" + bucketName + "/" + key);
    }
}

