lessons.fullSolution:
public class Main {
    public static void main(String[] args) {
        char grade = 'B';
        String comment; // It's better to not initialize here

        // Use a switch statement to set the 'comment' variable.
        // case 'A': "Excellent!"
        // case 'B': "Good job!"
        // case 'C': "You passed."
        // default: "Invalid grade."
        // Don't forget the 'break' statements!
        switch (grade) {
            case 'A':
                comment = "Excellent!";
                break;
            case 'B':
                comment = "Good job!";
                break;
            case 'C':
                comment = "You passed.";
                break;
            default:
                comment = "Invalid grade.";
                break;
        }

        System.out.println("Comment: " + comment);
    }
}

