public class Main {
    public static void main(String[] args) {
        int[] scores = {10, 12, 8, 15, 7};

        int total = 0;
        int max = scores[0];
        for (int score : scores) {
            total += score;
            if (score > max) {
                max = score;
            }
        }

        System.out.println(total);
        System.out.println(max);
    }
}

