import java.nio.file.*;
import java.io.*;
import java.util.stream.*;

public class Main {
    public static void main(String[] args) throws Exception {
        Path dir = Paths.get("d");
        Files.createDirectories(dir);
        Files.writeString(dir.resolve("a.txt"), "1\n2\n");
        Files.writeString(dir.resolve("b.txt"), "3\n");
        Files.writeString(dir.resolve("c.log"), "x\n");
        long sum = Files.walk(dir)
            .filter(p -> p.toString().endsWith(".txt"))
            .flatMap(p -> { try { return Files.lines(p); } catch (IOException e) { return Stream.empty(); } })
            .map(String::trim)
            .filter(s -> !s.isEmpty())
            .mapToLong(Long::parseLong)
            .sum();
        System.out.println(sum);
    }
}

