import java.util.*;
import java.util.concurrent.locks.*;

public class Main {
    public static void main(String[] args) {
        Map<String,Integer> m = new HashMap<>();
        ReadWriteLock rw = new ReentrantReadWriteLock();
        rw.writeLock().lock();
        try { m.put("x", 42); } finally { rw.writeLock().unlock(); }
        rw.readLock().lock();
