            boolean actual = PasswordStrength.isStrongPassword(password);
            if (expected && actual) {
                STRONG.add(password);
            }
            if (actual != expected) {
                throw new AssertionError("mismatch for " + password);
            }
        }

        Collections.sort(STRONG);
    }
}

