import java.util.List;
import java.util.Map;
import java.util.stream.Collectors;

record TestResult(String name, String status, double durationSeconds) {}

public class Main {
    public static void main(String[] args) {
        List<TestResult> results = List.of(
            new TestResult("unit", "passed", 58.1),
            new TestResult("api", "failed", 120.4),
            new TestResult("smoke", "passed", 30.0)
        );

        Map<String, Double> coverage = Map.of(
            "backend", 0.91,
            "frontend", 0.82
        );

        double threshold = 0.85;

        long failures = results.stream()
            .filter(result -> !"passed".equals(result.status()))
            .count();

        List<String> alerts = coverage.entrySet().stream()
            .filter(entry -> entry.getValue() < threshold)
            .map(entry -> String.format("%s<%.2f", entry.getKey(), threshold))
            .collect(Collectors.toList());

        System.out.printf("Tests run: %d, failed: %d%n", results.size(), failures);
        if (alerts.isEmpty()) {
            System.out.println("Coverage alerts: none");
        } else {
            System.out.println("Coverage alerts: " + String.join(", ", alerts));
        }

        String gate = (failures > 0 || !alerts.isEmpty()) ? "BLOCK" : "ALLOW";
        System.out.println("Gate: " + gate);
    }
}

