import java.util.ArrayList;
import java.util.List;
import java.util.Map;
import java.util.function.Function;
import java.util.stream.Collectors;

record Component(String name, String layer, List<String> depends) {}

public class Main {
    public static void main(String[] args) {
        List<Component> components = List.of(
            new Component("ui.dashboard", "presentation", List.of("service.reports")),
            new Component("service.reports", "service", List.of("domain.analytics", "infra.db")),
            new Component("domain.analytics", "domain", List.of("domain.models")),
            new Component("domain.models", "domain", List.of()),
            new Component("service.billing", "service", List.of("ui.dashboard", "domain.ledger")),
            new Component("domain.ledger", "domain", List.of("infra.db")),
            new Component("domain.audit", "domain", List.of("service.reports", "infra.db")),
            new Component("infra.db", "infrastructure", List.of())
        );

        Map<String, Integer> layerOrder = Map.of(
            "presentation", 3,
            "service", 2,
            "domain", 1,
            "infrastructure", 0
        );

        Map<String, Component> byName = components.stream()
            .collect(Collectors.toMap(Component::name, Function.identity()));

        List<String> violations = new ArrayList<>();

        for (Component component : components) {
            int fromLevel = layerOrder.get(component.layer());
            for (String dependency : component.depends()) {
                Component target = byName.get(dependency);
                if (target == null) {
                    continue;
                }
                int toLevel = layerOrder.get(target.layer());
                if (fromLevel < toLevel) {
                    violations.add(String.format(
                        "- %s -> %s (%s -> %s)",
                        component.name(),
                        dependency,
                        component.layer(),
                        target.layer()
                    ));
                }
            }
        }

        System.out.printf("Modules checked: %d%n", components.size());
        if (violations.isEmpty()) {
            System.out.println("Violations: none");
        } else {
            System.out.println("Violations:");
            for (String violation : violations) {
                System.out.println(violation);
            }
        }

        String status = violations.isEmpty() ? "ALIGNED" : "REFACTOR";
        System.out.println("Status: " + status);
    }
}

