import java.util.LinkedHashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        Map<String, double[]> metrics = new LinkedHashMap<>();
        metrics.put("latency_p95_ms", new double[]{340, 360});
        metrics.put("error_rate_pct", new double[]{0.7, 0.9});
        metrics.put("cpu_pct", new double[]{52, 49});

        System.out.println("Canary Analysis");
        for (Map.Entry<String, double[]> entry : metrics.entrySet()) {
            double baseline = entry.getValue()[0];
            double canary = entry.getValue()[1];
            double delta = canary - baseline;
            System.out.printf(
                "%s: baseline=%s, canary=%s, delta=%+.2f%n",
                entry.getKey(),
                formatValue(baseline),
                formatValue(canary),
                delta
            );
        }
    }

    private static String formatValue(double value) {
        if (Math.abs(value - Math.rint(value)) < 0.0001) {
            return String.format("%.0f", value);
        }
        return String.format("%.2f", value);
    }
}

