        List<List<Integer>> groups = List.of(
            List.of(1, 2),
            List.of(3, 4)
        );

        String flattened = groups.stream()
            .mapMulti((list, downstream) -> {
                for (Integer value : list) {
                    downstream.accept(value);
                }
            })
            .map(String::valueOf)
            .collect(Collectors.joining(" "));
        System.out.println(flattened);
    }
}

