        String summary = numbers.stream()
            .collect(Collectors.teeing(
                Collectors.minBy(Integer::compareTo),
                Collectors.maxBy(Integer::compareTo),
                (min, max) -> "min=" + min.orElseThrow() + " max=" + max.orElseThrow()
            ));
        System.out.println(summary);
    }
}

