import java.util.*;

public class Main {
    public static void main(String[] args) {
        double[] data = {5, 2, 8, 4, 9, 1};
        Arrays.sort(data);

        double q1 = inclusiveQuantile(data, 0.25);
        double q3 = inclusiveQuantile(data, 0.75);
        System.out.println(q1 + "," + q3);
    }

    private static double inclusiveQuantile(double[] data, double p) {
        int n = data.length;
        double h = (n - 1) * p + 1;
        int lowerIndex = (int) Math.floor(h) - 1;
        double fraction = h - Math.floor(h);
        if (lowerIndex < 0) {
            return data[0];
        }
        if (lowerIndex >= n - 1) {
            return data[n - 1];
        }
        double lower = data[lowerIndex];
        double upper = data[lowerIndex + 1];
        return lower + fraction * (upper - lower);
    }
}

