sealed interface AuthEvent permits Success, Failure, Retry {}
record Success(String user) implements AuthEvent {}
record Failure(int code) implements AuthEvent {}
record Retry(int attempt, Throwable lastError) implements AuthEvent {}

public class Main {
    public static void main(String[] args) {
        AuthEvent event = new Retry(3, new IllegalStateException("timeout"));

        String message;
        if (event instanceof Success success) {
            message = "Authenticated " + success.user();
        } else if (event instanceof Failure failure) {
            if (failure.code() >= 500) {
                message = "Permanent failure (" + failure.code() + ")";
            } else {
                message = "Temporary failure (" + failure.code() + ")";
            }
        } else if (event instanceof Retry retry) {
            message = "Retry attempt " + retry.attempt() + ": " + retry.lastError().getMessage();
        } else {
            throw new IllegalStateException("Unknown event: " + event);
        }

        System.out.println(message);
    }
}

