import java.util.List;
import java.util.concurrent.Callable;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.Future;

public class Main {
    static final ThreadLocal<String> REQUEST_ID = new ThreadLocal<>();

    public static void main(String[] args) throws InterruptedException, ExecutionException {
        Callable<String> inventory = wrapWithRequest("req-007", () -> serviceLine("inventory"));
        Callable<String> pricing = wrapWithRequest("req-007", () -> serviceLine("pricing"));

        ExecutorService executor = Executors.newFixedThreadPool(2);
        try {
            List<Future<String>> futures = executor.invokeAll(List.of(inventory, pricing));
            for (Future<String> future : futures) {
                System.out.println(future.get());
            }
        } finally {
            executor.shutdown();
        }
    }

    static Callable<String> wrapWithRequest(String requestId, Callable<String> task) {
        return () -> {
            REQUEST_ID.set(requestId);
            try {
                return task.call();
            } finally {
                REQUEST_ID.remove();
            }
        };
    }

    static String serviceLine(String service) {
        return service + " -> " + REQUEST_ID.get();
    }
}

