import java.util.List;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;

public class Main {
    public static void main(String[] args) throws InterruptedException {
        List<String> services = List.of("inventory", "pricing", "shipping");
        CountDownLatch latch = new CountDownLatch(services.size());
        Map<String, String> ready = new ConcurrentHashMap<>();
        ExecutorService executor = Executors.newFixedThreadPool(services.size());

        try {
            for (String service : services) {
                executor.submit(() -> {
                    try {
                        TimeUnit.MILLISECONDS.sleep(service.length() * 10L);
                        ready.put(service, service + " primed");
                    } catch (InterruptedException e) {
                        Thread.currentThread().interrupt();
                    } finally {
                        latch.countDown();
                    }
                });
            }

            latch.await();
            for (String service : services) {
                System.out.println(ready.get(service));
            }
            System.out.println("all services go");
        } finally {
            executor.shutdownNow();
        }
    }
}

