import java.util.concurrent.CompletableFuture;

public class Main {
    static String fetch(boolean fail) {
        if (fail) {
            throw new IllegalStateException("analytics offline");
        }
        return "analytics ok";
    }

    public static void main(String[] args) {
        String status = CompletableFuture
                .supplyAsync(() -> fetch(true))
                .handle((value, ex) -> {
                    if (ex == null) {
                        return "OK: " + value;
                    }
                    Throwable cause = ex instanceof java.util.concurrent.CompletionException ce ? ce.getCause() : ex;
                    return "FAIL: " + cause.getMessage();
                })
                .join();
        System.out.println(status);
    }
}

