import java.util.List;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.Phaser;
import java.util.concurrent.TimeUnit;

public class Main {
    public static void main(String[] args) throws Exception {
        List<String> services = List.of("inventory", "pricing", "shipping");
        Phaser phaser = new Phaser(1); // register main
        ExecutorService executor = Executors.newFixedThreadPool(services.size());

        try {
            for (String service : services) {
                phaser.register();
                executor.submit(() -> {
                    try {
                        TimeUnit.MILLISECONDS.sleep(service.length() * 5L);
                        System.out.println(service + " prepped");
                        phaser.arriveAndAwaitAdvance();
                        TimeUnit.MILLISECONDS.sleep(service.length() * 2L);
                        System.out.println(service + " go");
                    } catch (InterruptedException e) {
                        Thread.currentThread().interrupt();
                    } finally {
                        phaser.arriveAndDeregister();
                    }
                });
            }

            phaser.arriveAndAwaitAdvance();
            System.out.println("gate open");
        } finally {
            phaser.arriveAndDeregister();
            executor.shutdown();
        }
    }
}

